import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch,
} from 'react-router-dom';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Login from './pages/LoginPage.jsx';
import Board from './pages/BoardPage.jsx';
import NotFound from './pages/NotFound';
import queryString from 'query-string';
import { setAccessToken } from './api/index.js';

const App = () => {
  if (queryString.parse(window.location.search).accessToken) {
    setAccessToken(queryString.parse(window.location.search).accessToken);
  }

  const onSilentRefresh = () => {};

  useEffect(() => {
    onSilentRefresh();
  });
  return (
    <DndProvider backend={HTML5Backend}>
      <Router>
        <div id='header'>
          <h2 id='header-title'>Korello</h2>
        </div>

        {/* <Redirect from='/?' to='/boards' /> */}
        <Switch>
          <Route exact path='/' render={() => <Login />} />
          <Route path='/boards' component={Board} />
          <Redirect from='/board/:id/cards' to='/boards' />
          <Route component={NotFound} />
        </Switch>
      </Router>
    </DndProvider>
  );
};

export default App;
